-- premake5.lua
-- generated by muhhae

workspace "ImguiApp"
    configurations { "Debug", "Release" }

SRCDIR = "src" --Source Code Directory
BUILDDIR = "bin" --Build Directory
OBJDIR = "obj" --Obj Directory

project "ImguiApp"
    language "C++" 

    cppdialect "C++latest"
    
    links {
        "include/libImguiSFML/Libs/ImguiSFML",
    }

    links {
        "opengl32","sfml-audio", "sfml-graphics", "sfml-network", "sfml-system", "sfml-window"
    }

    targetdir "%{BUILDDIR}/%{cfg.buildcfg}" 
    objdir "%{OBJDIR}/%{cfg.buildcfg}"

    includedirs {
        "include/libImguiSFML/header"
    }

    files {
        "%{SRCDIR}/**.h",
        "%{SRCDIR}/**.hpp", 
        "%{SRCDIR}/**.cpp"
    }

    filter "configurations:Debug"
        kind "ConsoleApp"
        defines { "DEBUG" }
        symbols "On"

    filter "configurations:Release"
        kind "WindowedApp"
        defines { "RELEASE" }
        optimize "On"